import React, { useState } from 'react';
import girl from '../../../../assets/img/girlLoad.png';

const MyBook = React.forwardRef((props, ref) => {
  const { books } = props;
  const [currentIndex, setCurrentIndex] = useState(0);

  const currentBook = books[currentIndex];
  return (
    <div>
      <div className='demoPage' ref={ref}>
        <h4>Título: {books.Title}</h4>
        <p className='Text_author'>Autor: {books.Author}</p>
        <p className='Text_Synopsis'>Sinopsis: {books.Synopsis}</p>
        <div className='Container_book'>
          {books.Flipbook !== 'Not_available' ? (
            <iframe
              className='Iframe'
              title={`Iframe for ${books.Title}`}
              src={books.Flipbook}
              allowFullScreen="true"
            ></iframe>
          ) : (
            <div className='Container_Load'>
              <img className="Image_load" src={girl} alt="Imagen de carga"/>
              <a href={books.Link} target="_blank" rel="noopener noreferrer">
                Archivo no disponible, accede a él aquí
              </a>
            </div>
          )}
        </div>
      </div>
    </div>
  );
});

export default MyBook;
