import { Component } from "react";
import "./NavbarStyles.css";
import { HashLink as Link } from 'react-router-hash-link';
import photo from '../assets/img/imagenav.jpeg'
// import { findRenderedDOMComponentWithClass } from "react-dom/test-utils";

import AccessibilityComponent from './Components/AccessibilityComponent';
import foto from '../src/assets/img/disableble logo.jpg';


class Navbar extends Component {
    
    state={clicked: false}
    handleClick = ()=>{
        this.setState({clicked:!this.state.clicked})

    }
    render () {
    return (
        <>
            <nav>
            <div className="accessibility-toggle">
                <button className="accessibility-button" onClick={handleAccessibilityToggle}>
                <img src={foto} alt="Ilustración de persona discapacitada" />
                </button>
                {showAccessibility && <AccessibilityComponent />}
                </div>
                <div>
                    <ul id="Navbar" className={this.state.clicked ? "#Navbar active" : "#Navbar"}>
                        <li><a className="active" href="/"> Inicio</a></li>
                        <li><Link to="/#about" smooth={true}>Sobre</Link></li>
                        <li><a href="/pageWave/Cards-First-Wave/First-Wave-Books">Oleada 1</a></li>
                        <li><a href="/pageWave/Cards-Second-Wave/Second-Wave-Books">Oleada 2</a></li>
                        <li><Link to="/#Footer" smooth={true}>Contacto</Link></li>
                    </ul>
                    
                </div>
                <div id="Mobile" onClick={this.handleClick}>
                    <i id="Bar" className={this.state.clicked ? "fas fa-times" : "fas fa-bars"}></i>
                </div>
            </nav>
            <img id="photonav" src={photo} alt="foto_menu" />
        </>
    )
}
}
export default Navbar;
