import React, { useState } from "react";

const AccessibilityComponent = () => {
  const [backgroundColor, setBackgroundColor] = useState("");
  const [textColor, setTextColor] = useState("");
  const [fontSize, setFontSize] = useState("");

  const handleBackgroundColorChange = (event) => {
    if (backgroundColor) {
      document.body.classList.remove(backgroundColor);
    }
    setBackgroundColor(event.target.value);
    if (event.target.value) {
      document.body.classList.add(event.target.value);
    }
  };

  const handleTextColorChange = (event) => {
    if (textColor) {
      document.body.classList.remove(textColor);
    }
    setTextColor(event.target.value);
    if (event.target.value) {
      document.body.classList.add(event.target.value);
    }
  };

  const handleFontSizeChange = (event) => {
    if (fontSize) {
      document.body.classList.remove(fontSize);
    }
    setFontSize(event.target.value);
    if (event.target.value) {
      document.body.classList.add(event.target.value);
    }
  };

  const colorOptions = [
    { value: "", label: "Seleccione un color" },
    { value: "background-white", label: "Blanco" },
    { value: "background-lightgray", label: "Gris Claro" },
    { value: "background-darkgray", label: "Gris" }
  ];

  const textColorOptions = [
    { value: "", label: "Seleccione un color" },
    { value: "text-black", label: "Negro" },
    { value: "text-darkgray", label: "Gris Oscuro" },
    { value: "text-mediumgray", label: "Gris Medio" }
  ];

  const fontSizeOptions = [
    { value: "", label: "Seleccione un tamaño" },
    { value: "font-normal", label: "Normal" },
    { value: "font-large", label: "Grande" },
    { value: "font-xlarge", label: "Muy Grande" }
  ];

  const renderOptions = (options) => {
    return options.map((option) => (
      <option key={option.value} value={option.value}>
        {option.label}
      </option>
    ));
  };

  return (
    <div>
      <h1>Accesibilidad Componente</h1>
      <label>
        Color de Fondo:
        <select value={backgroundColor} onChange={handleBackgroundColorChange}>
          {renderOptions(colorOptions)}
        </select>
      </label>
      <br />
      <label>
        Color de Texto:
        <select value={textColor} onChange={handleTextColorChange}>
          {renderOptions(textColorOptions)}
        </select>
      </label>
      <br />
      <label>
        Tamaño de Texto:
        <select value={fontSize} onChange={handleFontSizeChange}>
          {renderOptions(fontSizeOptions)}
        </select>
      </label>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce suscipit
        libero et nibh feugiat, vel hendrerit metus efficitur.
      </p>
    </div>
  );
};

export default AccessibilityComponent;
