import React, { useState } from "react";

const AccessibilityComponent = () => {
  const [backgroundColors, setBackgroundColors] = useState([
    "white",
    "lightgray",
    "darkgray"
  ]);

  const [textColors, setTextColors] = useState([
    "black",
    "darkgray",
    "gray"
  ]);

  const [fontSizes, setFontSizes] = useState(["16px", "18px", "20px"]);

  const [backgroundIndex, setBackgroundIndex] = useState(0);
  const [textIndex, setTextIndex] = useState(0);
  const [fontSizeIndex, setFontSizeIndex] = useState(0);

  const handleBackgroundChange = () => {
    setBackgroundIndex((prevIndex) => (prevIndex + 1) % backgroundColors.length);
    document.body.style.backgroundColor = backgroundColors[backgroundIndex];
  };

  const handleTextColorChange = () => {
    setTextIndex((prevIndex) => (prevIndex + 1) % textColors.length);
    document.body.style.color = textColors[textIndex];
  };

  const handleFontSizeChange = () => {
    setFontSizeIndex((prevIndex) => (prevIndex + 1) % fontSizes.length);
    document.body.style.fontSize = fontSizes[fontSizeIndex];
  };

  return (
    <div>
      <h1>Accesibilidad Componente</h1>
      <button onClick={handleBackgroundChange}>Cambiar Fondo</button>
      <button onClick={handleTextColorChange}>Cambiar Color de Texto</button>
      <button onClick={handleFontSizeChange}>Cambiar Tamaño de Fuente</button>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce suscipit
        libero et nibh feugiat, vel hendrerit metus efficitur.
      </p>
    </div>
  );
};

export default AccessibilityComponent;
