import React, { useState } from 'react';
import { BrowserRouter, Route } from 'react-router-dom';
import Navbar from './Components/Navbar';
import PageWave from './Page/pageWave/pageWave';
import Home from './Page/homePage/Home';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import AccessibilityComponent from './Components/AccessibilityComponent';
import foto from '../src/assets/img/disableble logo.jpg';
<div className="accessibility-toggle">
<button className="accessibility-button" onClick={handleAccessibilityToggle}>
  <img src={foto} alt="Ilustración de persona discapacitada" />
</button>
{showAccessibility && <AccessibilityComponent />}
</div>


const App = () => {
  const [showAccessibility, setShowAccessibility] = useState(false);

  const handleAccessibilityToggle = () => {
    setShowAccessibility(!showAccessibility);
  };

  return (
    <BrowserRouter>
      <div className="app-container">

        <div className="sticky-navbar">
          <Navbar />
        </div>
        <div className="content">
          <Route path="/" exact component={Home} />
          <Route path="/pageWave/:cardsProp/:booksProp" component={PageWave} />
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
