import React, { useState } from "react";

const AccessibilityComponent = () => {
  const [backgroundColor, setBackgroundColor] = useState("");
  const [textColor, setTextColor] = useState("");
  const [fontSize, setFontSize] = useState("");

  const handleBackgroundColorChange = (color) => {
    document.body.style.backgroundColor = color;
    setBackgroundColor(color);
  };

  const handleTextColorChange = (color) => {
    document.body.style.color = color;
    setTextColor(color);
  };

  const handleFontSizeChange = (size) => {
    document.body.style.fontSize = size;
    setFontSize(size);
  };

  return (
    <div>
      <h1>Accesibilidad Componente</h1>
      <button onClick={() => handleBackgroundColorChange("white")}>
        Fondo Blanco
      </button>
      <button onClick={() => handleBackgroundColorChange("lightgray")}>
        Fondo Gris Claro
      </button>
      <button onClick={() => handleBackgroundColorChange("darkgray")}>
        Fondo Gris
      </button>
      <br />
      <button onClick={() => handleTextColorChange("black")}>
        Texto Negro
      </button>
      <button onClick={() => handleTextColorChange("darkgray")}>
        Texto Gris Oscuro
      </button>
      <button onClick={() => handleTextColorChange("gray")}>
        Texto Gris Medio
      </button>
      <br />
      <button onClick={() => handleFontSizeChange("16px")}>
        Tamaño Normal
      </button>
      <button onClick={() => handleFontSizeChange("18px")}>
        Tamaño Grande
      </button>
      <button onClick={() => handleFontSizeChange("20px")}>
        Tamaño Muy Grande
      </button>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce suscipit
        libero et nibh feugiat, vel hendrerit metus efficitur.
      </p>
    </div>
  );
};

export default AccessibilityComponent;
