import React, { useState } from "react";

const AccessibilityComponent = () => {
  const [backgroundColor, setBackgroundColor] = useState("#ffffff");
  const [textColor, setTextColor] = useState("#000000");

  const handleBackgroundColorChange = (event) => {
    document.body.style.backgroundColor = event.target.value;
    setBackgroundColor(event.target.value);
  };

  const handleTextColorChange = (event) => {
    document.body.style.color = event.target.value;
    setTextColor(event.target.value);
  };

  return (
    <div>
      <h1>Accesibilidad Componente</h1>
      <label>
        Color de Fondo:
        <select value={backgroundColor} onChange={handleBackgroundColorChange}>
          <option value="#ffffff">Blanco</option>
          <option value="#f0f0f0">Gris Claro</option>
          <option value="#000000">Gris</option>
        </select>
      </label>
      <br />
      <label>
        Color de Texto:
        <select value={textColor} onChange={handleTextColorChange}>
          <option value="#000000">Negro</option>
          <option value="#333333">Gris Oscuro</option>
          <option value="#666666">Gris Medio</option>
        </select>
      </label>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce suscipit
        libero et nibh feugiat, vel hendrerit metus efficitur.
      </p>
    </div>
  );
};

export default AccessibilityComponent;
