import React, { useState } from 'react';

const BookDropdown = ({ books, onBookSelect }) => {
  const [selectedBook, setSelectedBook] = useState('');

  const handleBookChange = (event) => {
    const selectedTitle = event.target.value;
    setSelectedBook(selectedTitle);
    onBookSelect(selectedTitle);
  };

  return (
    <div>
      <label htmlFor="bookSelect">Selecciona un libro:</label>
      <select id="bookSelect" value={selectedBook} onChange={handleBookChange}>
        <option value="">-- Elige un libro --</option>
        {books.map((book, index) => (
          <option key={index} value={book.Title}>
            {book.Title}
          </option>
        ))}
      </select>
    </div>
  );
};

export default BookDropdown;
